mapper.insert:
insert into tb_notice( 
	 notice_no, title, content, writer
) values (
	 #{notice_no}, #{title}, #{content}, #{writer}
)

mapper.update:
update tb_notice
   set title = #{title},
       content = #{content}
 where notice_no = #{notice_no}

update tb_notice
   set view_cnt = view_cnt + 1
 where notice_no = #{notice_no}
