mapper.insert:
insert into tb_notice(
	notice_no, title, content, writer
) values (
	s_notice_no.nextval, #{title}, #{content}, #{writer}
)

mapper.update:
update tb_notice
   set title = #{title},
       content = #{content}
 where no = #{no}

update tb_notice
   set view_cnt = view_cnt + 1
 where no = #{no}
